// 文ノード（345次元 + ジャンル適応情報付与）
CREATE (s1:Sentence {id: 1, text: '海風のメロディ夕陽が水平線を金色に染める湘南の海岸で、健太は彼女を待っていた。', aesthetic_quality: 0.010006853626149129, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s2:Sentence {id: 2, text: '潮風が頬を撫でていく中、砂浜に足跡を残しながら歩いてくる美しいシルエットが見える。', aesthetic_quality: 1.0, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s3:Sentence {id: 3, text: '「遅くなってごめんなさい」振り返ると、そこには完璧な微笑みを浮かべた麗華が立っていた。', aesthetic_quality: 0.0, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s4:Sentence {id: 4, text: '夕日の光が彼女の艶やかな髪を輝かせ、まるで天使のように見える。', aesthetic_quality: 0.003468102626262624, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s5:Sentence {id: 5, text: 'でも健太は知っている—彼女の心臓が鼓動を刻まないことを。', aesthetic_quality: 1.0, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s6:Sentence {id: 6, text: '彼女の温かな手が、実は精密な人工筋肉であることを。', aesthetic_quality: 0.0036604970408080795, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s7:Sentence {id: 7, text: '「大丈夫だよ。', aesthetic_quality: 0.0, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s8:Sentence {id: 8, text: '君を見てるだけで十分だから」健太は彼女の手を取った。', aesthetic_quality: 0.0036962672727272714, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s9:Sentence {id: 9, text: 'その感触は人間と何も変わらない。', aesthetic_quality: 0.0, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s10:Sentence {id: 10, text: 'いや、むしろもっと完璧かもしれない。', aesthetic_quality: 0.0, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s11:Sentence {id: 11, text: '適度な温度、柔らかさ、そして彼女だけが持つ独特の優しさ。', aesthetic_quality: 0.0038012853885314667, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s12:Sentence {id: 12, text: '「海って不思議ですね。', aesthetic_quality: 0.0005717036761053154, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s13:Sentence {id: 13, text: '同じように見えて、毎日違う表情を見せてくれる」麗華の瞳が海を見つめている。', aesthetic_quality: 0.00043291768025078366, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s14:Sentence {id: 14, text: 'その瞳の奥には、最先端の画像認識システムが隠されているはずなのに、健太にはただ純粋な美しさしか感じられなかった。', aesthetic_quality: 0.5368245754692965, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s15:Sentence {id: 15, text: '「君も同じだよ。', aesthetic_quality: 0.0, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s16:Sentence {id: 16, text: '毎日新しい君を発見する」二人は波打ち際を歩いた。', aesthetic_quality: 0.0018665554873054872, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s17:Sentence {id: 17, text: '足元で波が踊り、遠くでカモメが鳴いている。', aesthetic_quality: 0.0, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s18:Sentence {id: 18, text: '麗華は突然立ち止まって、砂浜に小さな貝殻を拾い上げた。', aesthetic_quality: 0.0, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s19:Sentence {id: 19, text: '「これ、きれいですね。', aesthetic_quality: 0.0, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s20:Sentence {id: 20, text: '自然が作り出した芸術品」「君の方がずっときれいだよ」健太の言葉に、麗華の頬がほんのり赤くなった。', aesthetic_quality: 0.00017387512071428572, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s21:Sentence {id: 21, text: '人工皮膚の下を流れる体液循環システムが、恥じらいの感情を完璧に再現している。', aesthetic_quality: 0.0, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s22:Sentence {id: 22, text: 'でも健太にとって、それは技術ではなく、愛する人の自然な反応だった。', aesthetic_quality: 0.8050758313879517, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s23:Sentence {id: 23, text: '空が紫色に変わり始めた頃、二人は防波堤に腰を下ろした。', aesthetic_quality: 0.00533901833914994, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s24:Sentence {id: 24, text: '麗華は健太の肩にそっと頭を預ける。', aesthetic_quality: 0.0, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s25:Sentence {id: 25, text: '「健太さん、私がロボットだということ、後悔していませんか？」「どうして？」「私には過去がないんです。', aesthetic_quality: 0.04882832085599999, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s26:Sentence {id: 26, text: '子供の頃の思い出も、家族の記憶も。', aesthetic_quality: 0.0022234905390094975, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s27:Sentence {id: 27, text: 'すべてがプログラムされたデータで...」健太は麗華の肩を優しく抱き寄せた。', aesthetic_quality: 0.0, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s28:Sentence {id: 28, text: '「過去なんてどうでもいい。', aesthetic_quality: 0.0, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s29:Sentence {id: 29, text: '大切なのは今、この瞬間だよ。', aesthetic_quality: 0.07269278767765149, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s30:Sentence {id: 30, text: '君といると、僕は生きていることを実感する。', aesthetic_quality: 0.0, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s31:Sentence {id: 31, text: 'それが人間だろうとロボットだろうと関係ない」麗華の瞳に、小さな水滴が光った。', aesthetic_quality: 0.00039024461157024793, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s32:Sentence {id: 32, text: '涙を流すプログラムなのか、それとも本当の感情なのか—もはやそんなことはどうでもよかった。', aesthetic_quality: 0.0, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s33:Sentence {id: 33, text: '「愛してる」健太の言葉に、麗華は初めて心の底から微笑んだ。', aesthetic_quality: 1.0, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s34:Sentence {id: 34, text: 'その笑顔は、どんな技術でも再現できない、純粋な愛の輝きに満ちていた。', aesthetic_quality: 0.8017843953, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s35:Sentence {id: 35, text: '星が瞬き始めた夜空の下、二人は永遠にこの瞬間が続けばいいと願いながら、波の音に包まれていた。', aesthetic_quality: 0.01854730732244318, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
CREATE (s36:Sentence {id: 36, text: '愛は、肉体を超越した魂の共鳴なのだから。', aesthetic_quality: 1.0, total_dimensions: 345, consciousness_level: 1.0, adaptive_weight: 0.75, genre_optimized: True});
